const App = () => {
  return (
    <Router>
      <Switch>
        {/* Public routes */}
        <Route path="/" element={<Homepage />} />
        {/* <Route path="/products" element={<Productpage />} /> */}
        <Route path="/login" element={<LoginPage />} />   
    
        {/* Private routes */}
        <PrivateRoute path="/products" element={<Productpage />} />
        
        {/* Default route */}
        {/* <Redirect to="/login" /> */}
      </Switch>
    </Router>
  );
};
export default App;
