/*==========================================================================
Name        : LoginForm.js
Assignment  : Assignment 5
Author(s)   : Jaimal Sahota (30126909), Xicheng(Justin) Wang (30191121)
Submission  : Mar 25, 2024
===========================================================================*/

import React, {useState, useEffect} from 'react';
// import SignupForm from './SignupForm.js';
import {useNavigate} from 'react-router-dom';
import { useAuthContext } from '../App.js';
import { addUser } from '../service/apiService.js';


const LoginForm = ({ switchSignup }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [authenticationMessage, setAuthenticationMessage] = useState('');
  const [authenticated, setAuthenticated] = useState("");
  // const [ authenticated, setAuthenticated] = useState([]);
  const [isInitialRender, setIsInitialRender] = useState(true);

  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();


  const loginEnter = () => {
    if (username.trim() === '' || password.trim() === '') {
      alert('Please fill in both Username and Password fields');
    } 
    else{
      setLoading(true);
      const apiCall = addUser({'username':username, 'password':password});
      apiCall.then(data => {
        setLoading(false);
        // console.log(data.data.authMessage);
        setAuthenticationMessage(data.data.authMessage); 
        setAuthenticated(data.data.authenticated); 
        
        // sessionStorage.setItem('authenticated', data.data.authenticated);
        

      })
      apiCall.then(data => {
        if (authenticated === true){
          alert('Login successful');
          console.log(authenticationMessage);
          // Navigate back
          navigate('/products');
        }
        else{
          alert(authenticationMessage);
          console.log(authenticationMessage);
        }
    })
      .catch(error => {
        console.error("Error saving product:", error);
        // setError('Failed to save product');
        setLoading(false);
      });

      // fetch('http://localhost:5000/login',{
      //     method: 'POST',
      //     headers: {
      //         'Content-Type': 'application/json',
      //     },
      //     body: JSON.stringify({'username':username, 'password':password}),
      // })
      // .then(response => {
      //     if (response.ok) {
      //         return response.json();
      //     } else {
      //         throw new Error('Authentication failed');
      //     }
      // })
      // .then(data => {setAuthenticationMessage(data.authMessage); setAuthenticated(data.authenticated); sessionStorage.setItem('authenticated', data.authenticated)})
      // .catch(error => setAuthenticationMessage(error));
    // }console.log(authenticationMessage)
    };
  };

    // useEffect(()=> {
    //     if (isInitialRender){
    //         setIsInitialRender(false)
    //     }
    //     else if (authenticated){
    //         navigate(`/Products`);
    //     }

    // },[authenticated])

    // const handleSubmit = (e) => {
    //   e.preventDefault();
    //   setLoading(true);
  
    //   const apiCall = productId ? editProduct(productId, product) : addProduct(product);
  
    //   apiCall.then(response => {
    //     setLoading(false);
    //     saveProduct(response.data);
    //     // Navigate back
    //     navigate(-1);
    //   })
    //   .catch(error => {
    //     console.error("Error saving product:", error);
    //     setError('Failed to save product');
    //     setLoading(false);
    //   });
    // };


  if (loading) return <div>Loading...</div>;
  return (
    <div>
      <h2>Login</h2>
      <div>
        <label>Username:</label>
        <input
          type="text"
          placeholder="Enter your username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div>
        <label>Password:</label>
        <input
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div>
        <button onClick={loginEnter}>Login</button>
      </div>
      <div>
        <button onClick={switchSignup}>Switch to Signup</button>
      </div>
    </div>
  );
};

export default LoginForm;
